from collections import Counter

def longestPalindrome(s):
    new_s = Counter(s)
    counter = 0 
    odd_counter = 0
    for i in new_s:
        if new_s[i] %2 ==0:
            counter += new_s[i]
        else:
            odd_counter = max(odd_counter , new_s[i])
    
    return counter + odd_counter
    
if __name__ == "__main__":
    s = ""
    print(longestPalindrome(s))